const { Client } = require("discord.js");
const paginationEmbed = async (msg, pages, emojiList = ['⏪', '⏩'], timeout = 120000,id,galleryCache) => {
	if (!msg && !msg.channel) throw new Error('Channel is inaccessible.');
	if (!pages) throw new Error('Pages are not given.');
	if (emojiList.length !== 2) throw new Error('Need two emojis.');
	let page = 0;
	const curPage = await msg.edit({embed:pages[page]});
	for (const emoji of emojiList) await curPage.react(emoji);
	const reactionCollector = curPage.createReactionCollector(
		(reaction, user) => emojiList.includes(reaction.emoji.name) && !user.bot,
		{ max:100,time: timeout,dispose:true}
	);
	reactionCollector.on('collect', reaction => {
		switch (reaction.emoji.name) {
			case emojiList[0]:
				page = page > 0 ? --page : pages.length - 1;
				break;
			case emojiList[1]:
				page = page + 1 < pages.length ? ++page : 0;
				break;
			default:
				break;
		}
		curPage.edit({embed:pages[page]});
	});
	reactionCollector.on('end', () => {
	galleryCache.deleteCache(`/ids/${id}`)
	console.log('done');
		if (!curPage.deleted) {
			// debugger
			// curPage.reactions.removeAll()
		}
	});
	return curPage;
};
module.exports = paginationEmbed;
